import { CometD, WebSocketTransport } from 'zetapush-cometd'
import { FetchLongPollingTransport } from './cometd'
import { getServers, shuffle } from './utils'
import { ConnectionStatusListener } from './connection-status'

/**
 * CometD Messages enumeration
 */
const Message = {
  RECONNECT_HANDSHAKE_VALUE: 'handshake',
  RECONNECT_NONE_VALUE: 'none',
  RECONNECT_RETRY_VALUE: 'retry'
}

/**
 * CometD Transports enumeration
 */
const Transport = {
  LONG_POLLING: 'long-polling',
  WEBSOCKET: 'websocket'
}

/**
 * Provide utilities and abstraction on CometD Transport layer
 * @access private
 */
export class ClientHelper {
  /**
   * Create a new ZetaPush client helper
   */
  constructor({ apiUrl, businessId, enableHttps = false, handshakeStrategy, resource }) {
    /**
     * @access private
     * @type {string}
     */
    this.businessId = businessId
    /**
     * @access private
     * @type {function():AbstractHandshakeManager}
     */
    this.handshakeStrategy = handshakeStrategy
    /**
     * @access private
     * @type {string}
     */
    this.resource = resource
    /**
     * @access private
     * @type {Promise}
     */
    this.servers = getServers({ apiUrl, businessId, enableHttps })
    /**
     * @access private
     * @type {Array<Object>}
     */
    this.connectionListeners = []
    /**
     * @access private
     * @type {boolean}
     */
    this.connected = false
    /**
     * @access private
     * @type {boolean}
     */
    this.wasConnected = false
    /**
     * @access private
     * @type {string}
     */
    this.serverUrl = null
    /**
     * @access private
     * @type {Array<Object>}
     */
    this.subscribeQueue = []
    /**
     * @access private
     * @type {CometD}
     */
    this.cometd = new CometD()
    this.cometd.registerTransport(Transport.WEBSOCKET, new WebSocketTransport())
    this.cometd.registerTransport(Transport.LONG_POLLING, new FetchLongPollingTransport())
    this.cometd.onTransportException = (cometd, transport) => {
      if (Transport.LONG_POLLING === transport) {
        // Try to find an other available server
        // Remove the current one from the _serverList array
        this.updateServerUrl()
      }
    }
    this.cometd.addListener('/meta/handshake', ({ ext, successful, advice, error }) => {
      console.debug('ClientHelper::/meta/handshake', { ext, successful, advice, error })
      if (successful) {
        const { authentication = null } = ext
        this.initialized(authentication)
      }
      else {
        // this.handshakeFailure(error)
      }
    })

    this.cometd.addListener('/meta/handshake', ({ advice, error, ext, successful }) => {
      console.debug('ClientHelper::/meta/handshake', { ext, successful, advice, error })
      // AuthNegotiation
      if (!successful) {
        if ('undefined' === typeof advice) {
          return
        }
        if (Message.RECONNECT_NONE_VALUE === advice.reconnect) {
          this.authenticationFailed(error)
        }
        else if (Message.RECONNECT_HANDSHAKE_VALUE === advice.reconnect) {
          this.negotiate(ext)
        }
      }
    })

    this.cometd.addListener('/meta/connect', ({ advice, channel, successful }) => {
      console.debug('ClientHelper::/meta/connect', { advice, channel, successful })
      // ConnectionListener
      if (this.cometd.isDisconnected()) {
        this.connected = false
        // Notify connection is closed
        this.connectionClosed()
      }
      else {
        this.wasConnected = this.connected
        this.connected = successful
        if (!this.wasConnected && this.connected) {
          this.cometd.batch(this, () => {
            // Unqueue subscriptions
            this.subscribeQueue.forEach(({ prefix, listener, subscriptions }) => {
              this.subscribe(prefix, listener, subscriptions)
            })
            this.subscribeQueue = []
          })
          // Notify connection is established
          this.connectionEstablished()
        }
        else if (this.wasConnected && !this.connected) {
          // Notify connection is broken
          this.connectionBroken()
        }
      }
    })
  }
  /**
   * Connect client using CometD Transport
   */
  connect() {
    this.servers.then((servers) => {
      this.serverUrl = shuffle(servers)

      this.cometd.configure({
        url: `${this.serverUrl}/strd`,
        backoffIncrement: 1000,
        maxBackoff: 60000,
        appendMessageTypeToURL: false
      })

      this.cometd.handshake(this.getHandshakeFields())
    })
  }
  /**
   * Notify listeners when connection is established
   */
  connectionEstablished() {
    this.connectionListeners.forEach((listener) => {
      listener.onConnectionEstablished()
    })
  }
  /**
   * Notify listeners when connection is broken
   */
  connectionBroken() {
    this.connectionListeners.forEach((listener) => {
      listener.onConnectionBroken()
    })
  }
  /**
   * Notify listeners when a message is lost
   */
  messageLost(channel, data) {
    this.connectionListeners.forEach((listener) => {
      listener.onMessageLost(channel, data)
    })
  }
  /**
   * Notify listeners when connection is closed
   */
  connectionClosed() {
    this.connectionListeners.forEach((listener) => {
      listener.onConnectionClosed()
    })
  }
  /**
   * Notify listeners when connection is established
   */
  initialized(authentication) {
    if (authentication) {
      this.userId = authentication.userId
    }
    this.connectionListeners.forEach((listener) => {
      listener.onSuccessfulHandshake(authentication)
    })
  }
  /**
   * Notify listeners when handshake step succeed
   */
  authenticationFailed(error) {
    this.connectionListeners.forEach((listener) => {
      listener.onFailedHandshake(error)
    })
  }
  /**
   * Manage handshake failure case
   */
  handshakeFailure() {

  }
  /**
  * Remove current server url from the server list and shuffle for another one
  */
  updateServerUrl() {
    this.servers.then((servers) => {
      const index = servers.indexOf(this.serverUrl)
      if (index > -1) {
        servers.splice(index, 1)
      }
      if (servers.length === 0) {
        // No more server available
      }
      else {
        this.serverUrl = shuffle(servers)
        this.cometd.configure({
          url: `${this.serverUrl}/strd`
        })
        setTimeout(() => {
          this.cometd.handshake(this.getHandshakeFields())
        }, 250)
      }
    })
  }
  /**
   * Negociate authentication
   */
  negotiate(ext) {
    console.debug('ClientHelper::negotiate', ext)
  }
  /**
   * Disconnect CometD client
   */
  disconnect() {
    this.cometd.disconnect()
  }
  /**
   * Get CometD handshake parameters
   * @return {Object}
   */
  getHandshakeFields() {
    const handshake = this.handshakeStrategy()
    return handshake.getHandshakeFields(this)
  }
  /**
   * Set a new handshake factory methods
   * @param {function():AbstractHandshakeManager} handshakeStrategy
   */
  setHandshakeStrategy(handshakeStrategy) {
    this.handshakeStrategy = handshakeStrategy
  }
  /**
   * Get business id
   * @return {string}
   */
  getBusinessId() {
    return this.businessId
  }
  /**
   * Get session id
   * @return {string}
   */
  getSessionId() {
    throw NotYetImplementedError()
  }
  /**
   * Get resource
   * @return {string}
   */
  getResource() {
    return this.resource
  }
  /**
   * Subribe all methods defined in the listener for the given prefixed channel
   * @param {string} prefix - Channel prefix
   * @param {Object} listener
   * @param {Object} subscriptions
   * @return {Object} subscriptions
   */
  subscribe(prefix, listener, subscriptions = {}) {
    if (this.cometd.isDisconnected()) {
      this.subscribeQueue.push({ prefix, listener, subscriptions })
    }
    else {
      for (const method in listener) {
        if (listener.hasOwnProperty(method)) {
          const channel = `${prefix}/${method}`
          subscriptions[method] = this.cometd.subscribe(channel, listener[method])
        }
      }
    }
    return subscriptions
  }
  /**
   * Get a publisher
   * @param {string} prefix - Channel prefix
   * @param {Object} definition
   * @return {Object} servicePublisher
   */
  createServicePublisher(prefix, definition) {
    const servicePublisher = {}
    for (const method in definition) {
      if (definition.hasOwnProperty(method)) {
        const channel = `${prefix}/${method}`
        servicePublisher[method] = (parameters = {}) => {
          this.cometd.publish(channel, parameters)
        }
      }
    }
    return servicePublisher
  }
  /**
   * Unsubcribe all subscriptions defined in given subscriptions object
   * @param {Object} subscriptions
   */
  unsubscribe(subscriptions) {
    for (const method in subscriptions) {
      if (subscriptions.hasOwnProperty(method)) {
        this.cometd.unsubscribe(subscriptions[method])
      }
    }
  }
  /**
   * Add a connection listener to handle life cycle connection events
   * @param {ConnectionStatusListener} listener
   */
  addConnectionStatusListener(listener) {
    const connectionListener = Object.assign(new ConnectionStatusListener(), listener)
    this.connectionListeners.push(connectionListener)
  }

}

import { ClientHelper } from './client-helper'

import { NotYetImplementedError } from './utils'

/**
 * Default ZetaPush API URL
 * @access public
 */
export const API_URL = 'https://api.zpush.io/'

/**
 * ZetaPush Client to connect
 * @access public
 * @example
 * const client = new Client({
 *   businessId: '<YOUR-BUSINESS-ID>',
 *   handshakeStrategy() {
 *     return AuthentFactory.createWeakHandshake({
 *       token: null,
 *       deploymentId: '<YOUR-DEPLOYMENT-ID>'
  *    })
 *   }
 * })
 * @example
 * const client = new Client({
 *   businessId: '<YOUR-BUSINESS-ID>',
 *   enableHttps: true,
 *   handshakeStrategy() {
 *     return AuthentFactory.createWeakHandshake({
 *       token: null,
 *       deploymentId: '<YOUR-DEPLOYMENT-ID>'
  *    })
 *   }
 * })
 */
export class Client {
  /**
   * Create a new ZetaPush client
   */
  constructor({ apiUrl = API_URL, businessId, enableHttps = false, handshakeStrategy, resource = null }) {
    /**
     * @access private
     * @type {ClientHelper}
     */
    this.helper = new ClientHelper({
      apiUrl,
      businessId,
      enableHttps,
      handshakeStrategy,
      resource
    })
  }
  /**
   * Connect client to ZetaPush
   */
  connect() {
    this.helper.connect()
  }
  /**
   * Disonnect client from ZetaPush
   */
  disconnect() {
    this.helper.disconnect()
  }
  /**
   * Create a service publisher based on publisher definition for the given deployment id
   * @return {Object}
   */
  createServicePublisher({ deploymentId, definition }) {
    return this.helper.createServicePublisher(`/service/${this.getBusinessId()}/${deploymentId}`, definition)
  }
  /**
   * Get the client business id
   * @return {string}
   */
  getBusinessId() {
    return this.helper.getBusinessId()
  }
  /**
   * Get the client resource
   * @return {string}
   */
  getResource() {
    return this.helper.getResource()
  }
  /**
   * Get the client user id
   * @return {string}
   */
  getUserId() {
    return this.helper.getUserId()
  }
  /**
   * Get the client session id
   * @return {string}
   */
  getSessionId() {
    return this.helper.getSessionId()
  }
  /**
   * Subscribe all methods described in the listener for the given deploymentId
   * @return {Object} subscription
   * @example
   * const stackServiceListener = {
   *   list() {},
   *   push() {},
   *   update() {}
   * }
   * client.subscribe({
   *   deploymentId: '<YOUR-STACK-DEPLOYMENT-ID>',
   *   listener: stackServiceListener
   * })
   */
  subscribe({ deploymentId, listener }) {
    return this.helper.subscribe(`/service/${this.getBusinessId()}/${deploymentId}`, listener)
  }
  /**
   * Create a publish/subscribe
   * @return {Object}
   */
  createPublisherSubscriber({ deploymentId, listener, definition }) {
    return {
      subscription: this.subscribe({ deploymentId, listener }),
      publisher: this.createServicePublisher({ deploymentId, definition })
    }
  }
  /**
   * Set new client resource value
   */
  setResource(resource) {
    this.helper.setResource(resource)
  }
  /**
   * Add a connection listener to handle life cycle connection events
   * @param {ConnectionStatusListener} listener
   */
  addConnectionStatusListener(listener) {
    return this.helper.addConnectionStatusListener(listener)
  }
  /**
   * Force disconnect/connect with new handshake factory
   * @param {function():AbstractHandshakeManager} handshakeStrategy
   */
  handshake(handshakeStrategy) {
    this.disconnect()
    if (handshakeStrategy) {
      this.helper.setHandshakeStrategy(handshakeStrategy)
    }
    this.connect()
  }

  /**
   * Get a service lister from methods list with a default handler
   * @return {Object} listener
   * @example
   * const getStackServiceListener = () => {
   *   return Client.getServiceListener({
   *     methods: ['getListeners', 'list', 'purge', 'push', 'remove', 'setListeners', 'update', 'error'],
   *     handler: ({ channel, data }) => {
   *       console.debug(`Stack::${method}`, { channel, data })
   *       document.querySelector(`form[name="${method}"] [name="output"]`).value = JSON.stringify(data)
   *     }
   *   })
   * }
   */
  static getServiceListener({ methods = [], handler = () => {} }) {
    return methods.reduce((listener, method) => {
      listener[method] = ({ channel, data }) => handler({ channel, data, method })
      return listener
    }, {})
  }

}

