import { Transport, LongPollingTransport } from 'zetapush-cometd'

/**
 * Implements LongPollingTransport using borwser fetch() API
 * @access private
 * @return {FetchLongPollingTransport}
 */
export function FetchLongPollingTransport() {
  const _super = new LongPollingTransport()
  const that = Transport.derive(_super)

  /**
   * Implements transport via fetch() API
   * @param {Object} packet
   */
  that.xhrSend = function (packet) {
    fetch(packet.url, {
      method: 'post',
      body: packet.body,
      headers: Object.assign(packet.headers, {
        'Content-Type': 'application/json;charset=UTF-8'
      })
    })
    .then((response) => {
      return response.json()
    })
    .then(packet.onSuccess)
    .catch(packet.onError)
  }

  return that
}

/**
 * Define life cycle connection methods 
 * @access public
 */
export class ConnectionStatusListener {
  /**
   * Callback fired when connection is broken
   */
  onConnectionBroken() {}
  /**
   * Callback fired when connection is closed
   */
  onConnectionClosed() {}
  /**
   * Callback fired when is established
   */
  onConnectionEstablished() {}
  /**
   * Callback fired when an error occurs in handshake step
   * @param {Object} error
   */
  onFailedHandshake(error) {}
  /**
   * Callback fired when a message is lost
   */
  onMessageLost() {}
  /**
   * Callback fired when handshake step succeed
   * @param {Object} authentication
   */
  onSuccessfulHandshake(authentication) {}
}

/**
 * ZetaPush deployables names
 */
const DeployableNames = {
  AUTH_SIMPLE: 'simple',
  AUTH_WEAK: 'weak',
  AUTH_DELEGATING: 'delegating'
}

/**
 * @access protected
 */
export class AbstractHandshakeManager {
  /**
   *
   */
  constructor({ authType, businessId, deploymentId }) {
    this.authType = authType
    this.businessId = businessId
    this.deploymentId = deploymentId
  }
  /**
   * @param {ClientHelper} client
   * @return {Object}
   */
  getHandshakeFields(client) {
    const authentication = {
      data: this.authData,
      type: `${client.getBusinessId()}.${this.deploymentId}.${this.authType}`,
      version: this.authVersion
    }
    if (client.getResource()) {
      authentication.resource = client.getResource()
    }
    return {
      ext: {
        authentication
      }
    }
  }
  /**
   * Get auth version
   * @return {string}
   */
  get authVersion() {
    return 'none'
  }

}

/**
 * @access public
 * @extends {AbstractHandshakeManager}
 */
export class TokenHandshakeManager extends AbstractHandshakeManager {
  /**
   *
   */
  constructor({ authType, deploymentId, token }) {
    super({ deploymentId, authType })
    this.token = token
  }
  /**
   * @return {token: string}
   */
  get authData() {
    const { token } = this
    return {
      token
    }
  }

}

/**
 * @access public
 * @extends {AbstractHandshakeManager}
 */
export class DefaultZetapushHandshakeManager extends AbstractHandshakeManager {

  /**
   *
   */
  constructor({ authType, deploymentId, login, password }) {
    super({ authType, deploymentId })
    this.login = login
    this.password = password
  }
  /**
   * Get auth data
   * @return {login: string, password: string}
   */
  get authData() {
    const { login, password } = this
    return {
      login, password
    }
  }

}

/**
 * Factory to create handshake
 * @access public
 */
export class AuthentFactory {
  /**
   * @return {DefaultZetapushHandshakeManager}
   */
  static createSimpleHandshake({ deploymentId, login, password }) {
    return AuthentFactory.createHandshake({
      authType: DeployableNames.AUTH_SIMPLE,
      deploymentId,
      login,
      password
    })
  }
  /**
   * @return {TokenHandshakeManager}
   */
  static createWeakHandshake({ deploymentId, token }) {
    return AuthentFactory.createHandshake({
      authType: DeployableNames.AUTH_WEAK,
      deploymentId,
      login: token,
      password: null
    })
  }
  /**
   * @return {TokenHandshakeManager}
   */
  static createDelegatingHandshake({ deploymentId, token }) {
    return AuthentFactory.createHandshake({
      authType: DeployableNames.AUTH_DELEGATING,
      deploymentId,
      login: token,
      password: null
    })
  }
  /**
   * @return {TokenHandshakeManager|DefaultZetapushHandshakeManager}
   */
  static createHandshake({ authType, deploymentId, login, password }) {
    if (null === password) {
      return new TokenHandshakeManager({ authType, deploymentId, token: login })
    }
    return new DefaultZetapushHandshakeManager({ authType, deploymentId, login, password  })
  }

}

import { Client } from './client'
import { AuthentFactory } from './handshake'
import { LocalStorageTokenPersistenceStrategy } from './token-persistence'

/**
 * @access protected
 * @extends {Client}
 */
export class SmartClient extends Client {
  /**
   * Create a new ZetaPush smart client
   */
  constructor({
    apiUrl, authenticationDeploymentId, businessId, enableHttps, resource = null,
    TokenPersistenceStrategy = LocalStorageTokenPersistenceStrategy
  }) {
    const handshakeStrategy = () => {
      const token = this.getToken()
      const handshake = AuthentFactory.createWeakHandshake({
        deploymentId: authenticationDeploymentId,
        token
      })
      return handshake
    }
    /**
     *
     */
    super({ apiUrl , businessId, enableHttps, handshakeStrategy, resource })
    const onSuccessfulHandshake = ({ publicToken, userId, token }) => {
      console.debug('SmartClient::onSuccessfulHandshake', { publicToken, userId, token })

      if (token) {
        this.strategy.set({ token })
      }
    }
    const onFailedHandshake = (error) => {
      console.debug('SmartClient::onFailedHandshake', error)
    }
    this.addConnectionStatusListener({ onFailedHandshake, onSuccessfulHandshake })
    /**
     * @access private
     * @type {TokenPersistenceStrategy}
     */
    this.strategy = new TokenPersistenceStrategy()
  }
  /**
   * @return {string} The stored token
   */
  getToken() {
    return this.strategy.get()
  }
}

/**
 * @type {string}
 */
const ZETAPUSH_TOKEN_KEY = 'zetapush.token'

/**
 * Provide abstraction for token persistence
 * @access protected
 */
export class AbstractTokenPersistenceStrategy {
  /**
   *
   */
  constructor({ key = ZETAPUSH_TOKEN_KEY } = {}) {
    /**
     * @access private
     * @type {string}
     */
    this.key = key
  }
  /**
   * @abstract
   * @return {string} The stored token
   */
  get() {}
  /**
   * @abstract
   */
  set({ token }) {}
}

/**
 * @access protected
 * @extends {AbstractTokenPersistenceStrategy}
 */
export class LocalStorageTokenPersistenceStrategy extends AbstractTokenPersistenceStrategy {
  /**
   * @override
   * @return {string} The stored token
   */
  get() {
    return localStorage.getItem(this.key)
  }
  /**
   * @override
   */
  set({ token }) {
    localStorage.setItem(this.key, token)
  }
}

/**
 * Match unsecure pattern web
 * @type {RegExp}
 */
const UNSECURE_PATTERN = /^http:\/\/|^\/\//

/**
 * @access private
 * @param {Array<Object>} list
 * @return {Object}
 */
export const shuffle = (list) => {
  const index = Math.floor(Math.random() * list.length)
  return list[index]
}

/**
 * @access private
 * @param {string} url
 * @param {boolean} enableHttps
 * @return {string}
 */
export const getSecureUrl = (url, enableHttps) => {
  return enableHttps ? url.replace(UNSECURE_PATTERN, 'https://') : url
}

/**
 * @access private
 * @return {Promise}
 */
export const getServers = ({ apiUrl, businessId, enableHttps }) => {
  const secureApiUrl = getSecureUrl(apiUrl, enableHttps)
  const url = `${secureApiUrl}${businessId}`
  return fetch(url)
    .then((response) => {
      return response.json()
    })
    .then(({ servers }) => {
      // TODO: Replace by a server side implementation when available
      return servers.map((server) => {
        return getSecureUrl(server, enableHttps)
      })
    })
}

/**
 * @access private
 * @extends {Error}
 */
export class NotYetImplementedError extends Error {
  /**
   * @param {string} message
   */
  constructor(message = '') {
    super(message)
    this.name = 'NotImplementedError'
  }

}
